  // WW/WL
  $(buttons[0]).on('click touchstart', function() {
    disableAllTools();
    forEachViewport(function(element) {
      cornerstoneTools.wwwc.activate(element, 1);
      cornerstoneTools.wwwcTouchDrag.activate(element);
    });
  });

  // Invert
